DROP TABLE IF EXISTS `egwk3_hymnal_book`;
CREATE TABLE `egwk3_hymnal_book` (
  `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `title` varchar(128) NOT NULL,
  `publisher` varchar(128) DEFAULT NULL,
  `year` varchar(32) DEFAULT NULL,
  `lang` varchar(16) DEFAULT NULL,
  `slug` varchar(128) DEFAULT NULL,
  `description` text DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `egwk3_hymnal_song`;
CREATE TABLE `egwk3_hymnal_song` (
  `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `hymnal_id` int(11) NOT NULL,
  `hymn_no` int(11) NOT NULL,
  `title` varchar(128) NOT NULL,
  `composer` varchar(255) DEFAULT NULL,
  `poet` varchar(255) DEFAULT NULL,
  `translation` varchar(255) DEFAULT NULL,
  `arranger` varchar(255) DEFAULT NULL,
  `tune` varchar(255) DEFAULT NULL,
  `tune_year` varchar(32) DEFAULT NULL,
  `lyrics_year` varchar(32) DEFAULT NULL,
  `scripture` varchar(128) DEFAULT NULL,
  `topic` varchar(128) DEFAULT NULL,
  `info` text DEFAULT NULL,
  `lily_score` text DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `egwk3_hymnal_verse`;
CREATE TABLE `egwk3_hymnal_verse` (
  `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `hymnal_id` int(11) NOT NULL,
  `hymn_no` int(11) NOT NULL,
  `verse_no` int(11) NOT NULL,
  `content` text DEFAULT NULL,
  `lily_hyphenated` text DEFAULT NULL,
  `note` text DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `egwk3_hymnal_synch`;
CREATE TABLE `egwk3_hymnal_synch` (
  `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `hymnal1_id` int(11) NOT NULL,
  `hymn1_no` int(11) NOT NULL,
  `hymnal2_id` int(11) NOT NULL,
  `hymn2_no` int(11) NOT NULL,
  `type` varchar(32) DEFAULT 'tune',
  `note` text DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

TRUNCATE TABLE egwk3_hymnal_book;
INSERT INTO `egwk3_hymnal_book` (`title`, `publisher`, `year`, `lang`, `slug`, `description`) VALUES
('Seventh-Day Adventist Hymnal', 'Review and Herald Publishing Association', '1985', 'en', 'sda-hymnal', 'This hymnal, published in 1985, includes 695 hymns and gospel songs. It contains the best of fine old hymns, early Advent hymns, contemporary hymns, favorites from the Church Hymnal, American folk hymns, modern gospel songs, compositions by Adventists.'),
('Hitünk énekei', 'Hetednapi Adventista Egyház', '1964', 'hu', 'hitunk-enekei', 'Gyülekezeti Énekeskönyv'),
('Nuevo Himnario Adventista', 'Pacific Press', '2010', 'es', 'nuevo-himnario-adventista', 'Himnario Adventista Del Septimo Dia'),
('Hymnes et Louanges', 'Vie et Sante', '2011', 'fr', 'hymnes-et-louanges', 'Recueil de cantiques a musique. Cantique a musique destine aux Adventistes du 7eme Jour de langue francaise.');

