(2, 1, 'Felséges Úr Isten'),
(2, 2, 'Az Úré minden tisztelet'),
(2, 3, 'Adj, Úr Isten, nékünk Szentlelket'),
(2, 4, 'Dicséret a Szentháromságnak'),
(2, 5, 'Téged, Isten, dicsérünk'),
(2, 6, 'Vezess minket, égi Atyánk'),
(2, 7, 'Téged, Isten, dicsérünk'),
(2, 8, 'Dicsérjed Istened, lelkem, szívem! '),
(2, 9, 'Az egek beszélik'),
(2, 10, 'Szent vagy, szent vagy, szent vagy'),
(2, 11, 'Azért, hogy oly szép a föld'),
(2, 12, 'Drága dolog az Úr Istent dicsérni,'),
(2, 13, 'Úrnak szolgái mindnyájan'),
(2, 14, 'Dicsérjétek az Urat!'),
(2, 15, 'A jóság Urát áldom én'),
(2, 16, 'Dicsérjük Istent, a jóság Istenét'),
(2, 17, 'Ím áldunk, nagy Úr'),
(2, 18, 'Áldja Isten szentségét mind'),
(2, 19, 'Úr Isten, néked zeng az ének'),
(2, 20, 'Imádjuk Őt'),
(2, 21, 'Miért ne zengném én az Istent'),
(2, 22, 'Ó! mely boldog ember az'),
(2, 23, 'Az Úr Sionban trónol'),
(2, 24, 'Mondjatok dicséretet'),
(2, 25, 'Az Isten fényes trónjánál'),
(2, 26, 'Úr Isten, mily nagy szent neved'),
(2, 27, 'Ó! bárcsak ezer ajkam volna'),
(2, 28, 'Jöjj, mondjunk hálaszót'),
(2, 29, 'Ha áldott kezed műveit'),
(2, 30, 'Itt van Isten köztünk'),
(2, 31, 'Dicsér Téged teljes szívem'),
(2, 32, 'Egek nagy Királya'),
(2, 33, 'Áldjad én lelkem, a dicsőség erős királyát'),
(2, 34, 'Ó! felséges Úr, mi kegyes Istenünk'),
(2, 35, 'Én Uram, Teremtőm, a Tied vagyok én'),
(2, 36, 'Ó! áldjuk most Őt, az égi Királyt'),
(2, 37, 'Adjatok hálákat az Úrnak'),
(2, 38, 'Ó! Ábrahám Ura'),
(2, 39, 'Perelj, Uram, perlőimmel'),
(2, 40, 'Én életemnek Istene'),
(2, 41, 'Irgalmazz, Úr Isten'),
(2, 42, 'Szívemet hozzád emelem'),
(2, 43, 'Mindenkoron áldom az én Uramat'),
(2, 44, 'Mely igen jó az Úr Istent dicsérni'),
(2, 45, 'Csak Téged dicsér mindenkor az ajkam'),
(2, 46, 'Tebenned bíztunk eleitől fogva'),
(2, 47, 'Én lelkem áldjad Istent'),
(2, 48, 'Soha el nem hagylak téged'),
(2, 49, 'A Sionnak hegyén'),
(2, 50, 'Nagy hálát adjunk az Atya Istennek'),
(2, 51, 'Hű karod véd és oltalmaz'),
(2, 52, 'Csak Te voltál oltalmunk'),
(2, 53, 'Gondviselő jó Atyám vagy'),
(2, 54, 'Nagy kegyelmed és jóságod'),
(2, 55, 'Száma nincsen, Uram, jótéteményidnek'),
(2, 56, 'Az Úr csodásan működik'),
(2, 57, 'Nagy kegyelmed árad, Istenünk, felénk'),
(2, 58, 'Isten, csodás nagy szerelmed'),
(2, 59, 'Én lelkem, zengjél szépen'),
(2, 60, 'Az Úr énnékem őriző pásztorom'),
(2, 61, 'Mily hatalmas vagy, égi Atyám!'),
(2, 62, 'Szeret az Isten'),
(2, 63, 'Jöjjetek, az Ige kér'),
(2, 64, 'Az oly emberek nyilván boldogok'),
(2, 65, 'Beszéld meg, mit tégyenek az ifjak'),
(2, 66, 'Én Istenem, taníts utaidra'),
(2, 67, 'Teljes szívből hozzád esedezem'),
(2, 68, 'Nagy békességük vagyon azoknak'),
(2, 69, 'Ó! ne vedd el szent igédet'),
(2, 70, 'Emeld fel szíved'),
(2, 71, 'Te égből származó'),
(2, 72, 'Hálaadásunkban rólad emlékezünk'),
(2, 73, 'Szent Bibliámat szeretem'),
(2, 74, 'Isten küldte drága szózat'),
(2, 75, 'Szép tündöklő hajnalcsillag'),
(2, 76, 'Csak néked él a szívem, lelkem'),
(2, 77, 'Zengd Jézus nevét, zengd világ'),
(2, 78, 'Szépséges Jézus'),
(2, 79, 'Uram Jézusom, örök vigaszom'),
(2, 80, 'Az én napom Jézus sötét éjszakán'),
(2, 81, 'Dicsőségnek szent királya'),
(2, 82, 'Üdvözöllek, Üdvözítőm, égő szívvel'),
(2, 83, 'Krisztusról zeng dalunk'),
(2, 84, 'Ó! halljad te föld és halljad te ég'),
(2, 85, 'Ó! üdvözítő Krisztus, Te égi pártfogónk'),
(2, 86, 'Légy mindörökre áldott'),
(2, 87, 'Áldott az egek Ura'),
(2, 88, 'Szép rózsabimbó nyílt ki'),
(2, 89, 'Úr Jézus Krisztus dicsérünk'),
(2, 90, 'Egy drága Gyermek született'),
(2, 91, 'Jer, Isten népe, áldjuk Őt'),
(2, 92, 'A mennyből jövök hozzátok'),
(2, 93, 'Az öröm napja ez a nap'),
(2, 94, 'Ó! Betlehem kis városa'),
(2, 95, 'Ó! nyíljatok meg szívkapuk'),
(2, 96, 'Én lelkem, készülj s örvend ma'),
(2, 97, 'Szeretet, ki teremtettél'),
(2, 98, 'E világot, bár ez bűnt tett'),
(2, 99, 'Szívünk vígsággal ma betelt'),
(2, 100, 'Új világosság jelenék'),
(2, 101, 'Légy hálás Jézus Krisztusnak'),
(2, 102, 'Az Isten annyira szerette a bűnös világot'),
(2, 103, 'Ki mindeneknek Atyja vagy'),
(2, 104, 'Elrejt az Úr'),
(2, 105, 'Az áldott orvos már közel'),
(2, 106, 'Jer, mindnyájan örüljünk'),
(2, 107, 'Mily édes vagy te szeretet'),
(2, 108, 'Isten drága, szent szerelme'),
(2, 109, 'Mennyei Ige'),
(2, 110, 'Kezdetben volt az Ige'),
(2, 111, 'A jóság az én pásztorom'),
(2, 112, 'Krisztus, ártatlan Bárány'),
(2, 113, 'A keresztfát ha nézem én'),
(2, 114, 'Ó! véráztatta szent fej'),
(2, 115, 'Isten örök szeretete'),
(2, 116, 'Áll szomorún a kopár hegy'),
(2, 117, 'Ha mardos bűnöm, vétkem'),
(2, 118, 'Jézus, élte életemnek'),
(2, 119, 'Ó! ártatlanság Báránya'),
(2, 120, 'Ó! Te nagy szenvedő'),
(2, 121, 'A Bárány Jézus hordozta'),
(2, 122, 'Ó! világ, nézd'),
(2, 123, 'Jézus Istennek Báránya'),
(2, 124, 'Áll a Krisztus szent keresztje'),
(2, 125, 'Alleluja! Ím feltámadt'),
(2, 126, 'Jézus Krisztus feltámadt! Alleluja!'),
(2, 127, 'Hívő lelkek, zengjetek'),
(2, 128, 'Jézus meghalt bűneinkért'),
(2, 129, 'A csillagos nagy ég'),
(2, 130, 'Drága égi kegyelem'),
(2, 131, 'Ó! Úr Jézus! Egeknek nagy Királya! '),
(2, 132, 'Egy drága forrást ismerek'),
(2, 133, 'Áldott légy Krisztus'),
(2, 134, 'Napban, holdban, mindenütt jelek'),
(2, 135, 'Jézus Krisztus jön a felhőn'),
(2, 136, 'Ó! Jézus a szívem úgy vár!'),
(2, 137, 'Néped oly várva vár!'),
(2, 138, 'Mondd, miért szunnyadsz'),
(2, 139, 'Ó! jöjj, ó! jöjj, Immánuel'),
(2, 140, 'Úgy várjuk visszajöttödet'),
(2, 141, 'Halld, ó! halld, mily hangos szó'),
(2, 142, 'Bizony meglészen az idő'),
(2, 143, 'A jöttöd miként várjam'),
(2, 144, 'Ébredj ember, hangzik folyton'),
(2, 145, 'Az erős Isten, uraknak Ura'),
(2, 146, 'Ó! Jeruzsálem, szép honom'),
(2, 147, 'A bánat éje nem örök'),
(2, 148, 'Nincs maradandó városunk'),
(2, 149, 'Az Úrnak áldott nagy kegyelme üdvöz'),
(2, 150, 'Mennyei otthonom: Éden'),
(2, 151, 'Jézus Krisztus bennem él!'),
(2, 152, 'Jézus, én bizodalmam és megváltóm'),
(2, 153, 'Drága hon, ragyogóbb, mint a nap'),
(2, 154, 'Jövel, Szentlélek Isten'),
(2, 155, 'Erőnek és hitnek Lelke'),
(2, 156, 'Jövel, Szentlélek Úr Isten, töltsd be szíveinket'),
