(2, 161, 'Szentlélek, végy körül bennünket'),
(2, 162, 'Isten Lelke, légy velem'),
(2, 163, 'Jövel, élet Lelke'),
(2, 164, 'Isten Lelke, béke Lelke'),
(2, 165, 'Égi áldás szent esője'),
(2, 166, 'Ne szállj perbe énvelem'),
(2, 167, 'Ím megtört szívvel állunk trónodnál'),
(2, 168, 'Légy irgalmas, Úr Isten'),
(2, 169, 'A töredelmes szívet, Te, Uram, szereted'),
(2, 170, 'Eltévedtem, mint juh'),
(2, 171, 'Bocsásd meg, Úr Isten, ifjúságomnak vétkét'),
(2, 172, 'Dicsőséges égi Felség!'),
(2, 173, 'Istenem, én nagy bűnös ember'),
(2, 174, 'Atya Úr Isten könyörülj rajtam'),
(2, 175, 'Minden népek hirdessétek'),
(2, 176, 'Felséges Isten, hozzád kiáltunk'),
(2, 177, 'Mint látod, hűtlen szolgaként'),
(2, 178, 'Hallgass meg minket, Úr Isten'),
(2, 179, 'Ím, nagy Isten, most előtted'),
(2, 180, 'Bús lelkem bántja bánat'),
(2, 181, '"Jézus téged visszavár!"'),
(2, 182, 'Már majdnem teljes benned a hit'),
(2, 183, 'Tiéd e szív!'),
(2, 184, 'Most fény tör át a tárt kapun'),
(2, 185, 'Te fáradt vándor, merre mégy?'),
(2, 186, 'Én élek! mondja Istened'),
(2, 187, 'Tudom és érzem, Jézus enyém'),
(2, 188, 'Tökéletes volt minden tekintetben'),
(2, 189, 'Ó! Jézus, árva csendben'),
(2, 190, 'Én érted haltam meg'),
(2, 191, 'Rád tekint már hitem'),
(2, 192, 'Tégy foglyoddá, Uram'),
(2, 193, 'Várj, ember szíve, készen!'),
(2, 194, 'Jöjj be, ó! jöjj be, szívem nyitva áll'),
(2, 195, 'Már hallom, Jézusom'),
(2, 196, 'Kegyes Jézus, én imádságomra'),
(2, 197, 'Hozzád kiáltok, én Istenem'),
(2, 198, 'Kész szívvel követünk most Téged'),
